/*
 * Cyberpunk Neon Floating Window Theme
 * Author: Your Name/AI
 * Version: 1.0
 */

/* ==========================================================================
   主窗口样式 (.cyberpunk-neon)
   ========================================================================== */
.cyberpunk-neon {
  /* -- 颜色变量 (方便维护) -- */
  --cp-bg: #1a1a2e;         /* 深蓝紫背景 */
  --cp-accent-yellow: #f0a500; /* 亮黄强调色 */
  --cp-accent-pink: #e4007f;   /* 霓虹粉强调色 */
  --cp-text: #ffffff;        /* 白色文字 */
  --cp-black: #000000;       /* 纯黑 */

  /* -- 整体容器样式 -- */
  background-color: var(--cp-bg);
  color: var(--cp-text);
  border: 1px solid var(--cp-accent-pink);
  border-radius: 4px; /* 小圆角 */
  font-family: 'Consolas', 'Monaco', 'monospace'; /* 使用等宽字体增强赛博朋克感 */
  overflow: hidden; /* 确保内部元素不会超出圆角范围 */

  /* -- 霓虹发光阴影效果 -- */
  /* 技巧: 多层阴影叠加创造更真实的霓虹辉光效果 */
  box-shadow: 
    0 0 2px var(--cp-text),            /* 内层白色细光，模拟灯管核心 */
    0 0 5px var(--cp-accent-pink),     /* 中层粉色辉光 */
    0 0 10px var(--cp-accent-pink),    /* 外层更弥散的粉色辉光 */
    0 0 20px var(--cp-accent-pink);

  /* 为了更好的展示效果，添加一些尺寸和定位 */
  position: fixed; /* 或 absolute, 根据您的需求 */
  top: 50px;
  left: 50px;
  width: 350px;
  min-height: 200px;
  display: flex;
  flex-direction: column;
}

/* ==========================================================================
   标题栏 (.fp-window-title)
   ========================================================================== */
.cyberpunk-neon .fp-window-title {
  background-color: var(--cp-bg); /* 虽然与父级相同，但明确指定以防万一 */
  padding: 8px 12px;
  font-weight: bold;
  cursor: move; /* 增加可拖动鼠标样式 */
  border-bottom: 1px solid var(--cp-accent-yellow); /* 底部的亮黄色边框 */
  user-select: none; /* 防止拖动时选中文本 */
}

/* ==========================================================================
   内容区 (.fp-window-body)
   ========================================================================== */
.cyberpunk-neon .fp-window-body {
  background-color: transparent; /* 透明背景 */
  padding: 16px 12px;
  flex-grow: 1; /* 让内容区填充剩余空间 */
}

/* ==========================================================================
   按钮 (.fp-window-body button)
   ========================================================================== */
.cyberpunk-neon .fp-window-body button {
  /* -- 基础样式 -- */
  background-color: var(--cp-black);
  color: var(--cp-text);
  border: 1px solid var(--cp-accent-yellow);
  padding: 8px 16px;

  /* -- 外观样式 -- */
  font-family: inherit; /* 继承父级字体 */
  font-size: 14px;
  border-radius: 0; /* 方形按钮 */
  cursor: pointer;
  outline: none; /* 移除点击时的轮廓 */

  /* -- 动效 -- */
  transition: background-color 0.2s ease-in-out, color 0.2s ease-in-out;
}

/* -- 按钮悬停效果 -- */
.cyberpunk-neon .fp-window-body button:hover {
  background-color: var(--cp-accent-yellow);
  color: var(--cp-black);
}

/* -- 按钮聚焦效果 (为了可访问性) -- */
.cyberpunk-neon .fp-window-body button:focus-visible {
    box-shadow: 0 0 5px var(--cp-accent-yellow);
}
