/*nini🍎玻璃粉苹果*/
/*绝对禁止二传商用，二改标注即可，但是不可直接用我的素材*/
/*部分代码来自于https://discord.com/channels/1291925535324110879/1380392618336194702，感谢各位老师分享*/
/*素材除头像框全部为本人绘制，不可随便使用*/
/*1.字体*/
@import url("https://fontsapi.zeoseven.com/116/main/result.css");

body {
    font-family: "Kingnammm Maiyuan 2";
    font-weight: normal;
}
/* 2.导航栏 */
#top-bar {
    background: linear-gradient(to bottom, rgba(255, 221, 229, 0.5), rgba(255, 255, 255, 0.2)) !important;
    backdrop-filter: blur(12px); 
    -webkit-backdrop-filter: blur(12px);
    border-radius: 15px;
    border: 1px solid rgba(255, 255, 255, 0.3); 
    box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1) !important;
}

/* 2.1替换前准备  */
#top-settings-holder .drawer-icon::before {
  content: '' !important;
  visibility: hidden;
}

#top-settings-holder .drawer {
  flex: 1;
}

/* 2.2图标样式 */
#top-settings-holder .drawer-icon {
  display: block;
  margin: 0 auto;
  width: 35px !important;
  height: 32px !important;
  background-size: 77px !important;
  background-position: center !important;
  background-repeat: no-repeat !important;
  opacity: 0.85 !important;
  transition: transform 0.3s ease-in-out, opacity 0.3s ease-in-out;
filter: drop-shadow(2px 1px 1px rgba(255, 180, 200, 0.9));
}

/* 【第1组图标】 */
#ai-config-button .drawer-icon,
#advanced-formatting-button .drawer-icon,
#logo_block .drawer-icon,
#backgrounds-drawer-toggle .drawer-icon,
#table_database_settings_drawer .drawer-icon,
#rightNavHolder .drawer-icon {
  background-image: url('https://p.sda1.dev/26/2d8f3ea41bb91e4324714ee57ce52991/1000023088.png');
}

/* 【第2组图标】 */
#sys-settings-button .drawer-icon,
#WI-SP-button .drawer-icon,
#extensions-settings-button .drawer-icon,
#persona-management-button .drawer-icon {
  background-image: url('https://p.sda1.dev/26/08a1a408c944e12e260565c82a7a261f/1000023089.png');
}

#user-settings-button .drawer-icon {
  background-image: url('https://p.sda1.dev/26/1c9ccd9ba74549f0202ce6f09aff65aa/1756093501117_edit_335283208776679.png') !important;
  background-size: contain !important;
  position: relative;
  z-index: 1;
  top: 3px;
  transform: scale(1.4);
}

/* 2.3 激活状态图标 */
#top-settings-holder .drawer-icon:not(.closedIcon) {
    background-image: url('https://p.sda1.dev/26/ce67a250c339b9033282a6f02a1b2ef2/1000023090.png') !important;
    opacity: 1 !important;
    background-size: 89px !important;
    transform: scale(1); 
    filter: none !important;
}

/* 3.设置展开后的抽屉背景图片 */
.drawer-content {
    background: linear-gradient(to bottom, rgba(255, 233, 238, 0.9), rgba(255, 255, 255, 0.6));
    backdrop-filter: blur(1px);
    -webkit-backdrop-filter: blur(10px); /* 兼容 Safari 浏览器 */
}
.drawer-content.openDrawer {
    max-height: calc(100dvh - var(--topBarBlockSize));
height: calc(100dvh - var(--topBarBlockSize));
}

#left-nav-panel,
#right-nav-panel {
    height: 100dvh;
    max-height: 100dvh;
}

@media (max-width: 600px) {
    #right-nav-panel {
        max-height: calc(100dvh - var(--topBarBlockSize));
    }
}
/* 4.滑块图标 */

/* --- 适用于 Chrome, Safari, Edge --- */
input[type="range"]::-webkit-slider-thumb {
  -webkit-appearance: none;
  appearance: none;
  width: 30px;
  height: 30px;
  cursor: pointer;
  border: none;
  background-color: transparent;
  background-image: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><text x="50%" y="50%" font-size="80" text-anchor="middle" dominant-baseline="central">🫧</text></svg>');
  background-repeat: no-repeat;
  background-position: center center;
  background-size: contain;
  margin-top: -12px; 
}

/* --- 适用于 Firefox --- */
input[type="range"]::-moz-range-thumb {
  width: 30px;
  height: 30px;
  cursor: pointer;
  border: none;
  background-color: transparent;
  background-image: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><text x="50%" y="50%" font-size="80" text-anchor="middle" dominant-baseline="central">🫧</text></svg>');
  background-repeat: no-repeat;
  background-position: center center;
  background-size: contain;
}

/* 5. 滑动条轨道  */
input[type="range"] {
  -webkit-appearance: none;
  appearance: none;
  width: 100%;
  cursor: pointer;
  background-color: transparent !important;
  border: none !important;
}

/* 为 Chrome, Safari, Edge 设计轨道样式 */
input[type="range"]::-webkit-slider-runnable-track {
  height: 6px; 
  border-radius: 6px;
  background: linear-gradient(to right, rgba(255, 221, 229, 1), rgba(255, 255, 255, 1));
}

/* 为 Firefox 设计轨道样式 */
input[type="range"]::-moz-range-track {
  height: 6px;
  border-radius: 6px;
  background: linear-gradient(to right, rgba(255, 221, 229, 1), rgba(255, 255, 255, 1));
  /* Firefox也需要移除边框 */
  border: none !important; 
}


/* 6.输入框 */
#form_sheld {
  position: absolute;
  bottom: 15px; /* 底栏与屏幕底部的间距 */
  left: 0;
  right: 0;
  width: 100%;
  pointer-events: none; 
}

/* 6.1输入框主体宽度 */
#send_form {
  width: 95%; /* 输入框宽度 */
  max-width: 820px; /* 限制最大宽度*/
  margin: 0 auto !important;
  pointer-events: auto; 
}
.last_mes {
  margin-bottom: 150px !important; 
}


/* 6.2. 输入框容器美化 */

/* 容器默认样式 */
#send_form, body.no-blur #send_form {
  border-radius: 25px;
  background: linear-gradient(to bottom, rgba(255, 240, 245, 0.5), rgba(255, 255, 255, 0.35)) !important;

  backdrop-filter: blur(15px); 
  -webkit-backdrop-filter: blur(15px);
  /* 内部图标与边框间距 */
  padding: 8px 10px;
  border: 1px solid rgba(255, 255, 255, 0.3);
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.15);
  transition: background 0.3s ease,border-color 0.3s ease,box-shadow 0.3s ease !important;
  display: flex;
  align-items: center;
}

/*悬停效果*/
#send_form:hover, body.no-blur #send_form:hover {
  transform: translateY(-5px);
  background: linear-gradient(to bottom, rgba(255, 240, 245, 0.65), rgba(255, 255, 255, 0.5)) !important;
  box-shadow: 0 8px 30px rgba(212, 163, 175, 0.4); 
  border: 1px solid rgba(255, 255, 255, 0.6);
}

/* 底栏图标之间的间距 */
#rightSendForm, #leftSendForm {
  gap: 5px;
}


/* 6.3. 打字区域 */

#send_textarea {
  background: linear-gradient(to bottom, rgba(255, 240, 245, 0.6), rgba(255, 255, 255, 0.4)) !important;
  border: 1.5px solid #E6D6D9; 
  border-radius: 18px; 
  box-shadow: inset 0 1px 3px rgba(0, 0, 0, 0.05);
  color: #3d3b4f;
  padding: 10px 18px; 
  font-size: 1rem;
  line-height: 1.6;
  outline: none; 
  resize: none;
  clip-path: none !important;
  transition: background 0.3s ease, border-color 0.3s ease, box-shadow 0.3s ease;
}

#send_textarea:focus {
  background: linear-gradient(to bottom, rgba(255, 240, 245, 0.8), rgba(255, 255, 255, 0.6)) !important;
  border-color: #D1B6BA;
  box-shadow: inset 0 1px 3px rgba(0, 0, 0, 0.05), 
              0 2px 0 0 rgba(212, 163, 175, 0.9);
}

#send_form:has(#send_textarea:focus-visible) {
  outline: none;
}


/* 6.4. 图标美化与交互 */

/* 统一尺寸 */
#options_button, #send_but, #extensionsMenuButton, #quick-reply-rocket-button {
    width: 29px !important;
    height: 39px !important;
    background-size: 33px !important;
    background-position: center;
    background-repeat: no-repeat;
    font-size: 0 !important;
    /* --- 修改点：只保留对 transform 的过渡 --- */
    transition: transform 0.2s ease-out;
}

/* 悬停效果 (这段无需改动) */
#options_button:hover, #send_but:hover, #extensionsMenuButton:hover, #quick-reply-rocket-button:hover {
    transform: scale(1.1);
}

/* 按钮图片资源 */
#options_button { background-image: url('https://p.sda1.dev/26/72343b5909204aba3426965f739e3477/1000023151.png'); background-size: 30px !important; }
#extensionsMenuButton { background-image: url('https://p.sda1.dev/26/b4f55b67c1663eb5765485be2b78205b/1000023153.png'); }
#quick-reply-rocket-button { background-image: url('https://p.sda1.dev/26/e9d11716685e639955c9a5991eb91675/1000023152.png'); background-size: 35px !important; }
#send_but { background-image: url('https://p.sda1.dev/26/228f34135a761584c414f1f75124255a/1000023154.png'); background-size: 33px !important; }

#qr--bar > .qr--buttons .qr--button {
  filter: none;
}
#options_button::before, #send_but::before, #extensionsMenuButton::before {
    content: '' !important; visibility: hidden;
}
#quick-reply-rocket-button > i { 
    display: none !important;
}
/* 6.5为“停止”按钮设置一个独立的、不被隐藏的样式 */
#mes_stop {
    font-size: 0 !important;
    width: 38px;
    height: 38px;
    background-image: url('https://p.sda1.dev/26/b5b6c26fd49566dd8713f9c1c5a40b53/1000023517.png');
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
    opacity: 1 !important;
}

/* 7. 聊天气泡 */

/* 外部容器 */
.mes {
    width: 97% !important; 
    max-width: 100% !important;
    margin: 0 auto !important;
    padding: 0 !important;
    position: relative;
    background: transparent !important;
    border: none !important;
    box-shadow: none !important;
    border-radius: 0 !important;
    overflow: visible !important;
}

/* 间距调整 */
#chat .mes {
    margin-top: 79px !important; 
    margin-bottom: 110px !important;
}
#chat .mes:first-child {
    margin-top: 140px !important; 
}
:root {
    --bubble-top-gap: 15px; /* 默认是0，不移动 */
}

/* 将这个上下间距应用到所有气泡上 */
.mes .mes_block {
    position: relative; /* 确保transform生效 */
    transform: translateY(var(--bubble-top-gap));
}

/* 外观 */
.mes_block {
    position: relative;
    border-radius: 20px !important; 
    border: 1px solid rgba(220, 220, 220, 0.3) !important; 
    padding: 2px !important; 
    box-shadow: 
        0 10px 35px rgba(0, 0, 0, 0.15),
        0 5px 15px rgba(0, 0, 0, 0.2),     
        inset 0 6px 12px rgba(255, 255, 255, 0.9), 
        inset 0 -2px 12px rgba(200, 200, 220, 0.3) !important; 
}

/* 背景渐变 */
.mes:not([is_user="true"]) .mes_block {
    background: linear-gradient(145deg, rgba(255, 200, 210, 0.4), rgba(255, 255, 255, 0.3)) !important;
}
.mes[is_user="true"] .mes_block {
    background: linear-gradient(145deg, rgba(255, 200, 210, 0.4), rgba(190, 210, 255, 0.4)) !important;
}

/* 气泡内边距 */
.mes_block {
    /*上下左右边距*/
    padding-top: 10px !important;    
    padding-right: 20px !important;  
    padding-bottom: 35px !important; 
    padding-left: 20px !important;  
}

/* 让消息框里的名称和文字铺满消息框的可用宽度 */
.mes_block .ch_name,
.mes_block .mes_text {
    width: 100%; /* 确保元素占据父元素内容区的全部宽度 */
    box-sizing: border-box; /* 包含内边距和边框在宽度计算内 */
    margin: 0; /* 移除所有外部边距 */
    padding-left: 0; /* 移除可能存在的左侧内边距 */
    padding-right: 0; /* 移除可能存在的右侧内边距 */
}

/* 气泡内文字样式 */
.mes_block .mes_text {
    text-align: justify; /* 文字两端对齐 */
    padding-top: calc(var(--mainFontSize) * 0.8); /* 增加与名称之间的距离 */
}

/* --- 新代码结束 --- */


/* 字体颜色 */
/* 用户消息文字颜色 */
.mes[is_user="true"] .mes_text {
  color: #B55D79; 
}
/* AI消息文字颜色 */
.mes:not([is_user="true"]) .mes_text {
  color: #7f5662; 
}

/* 8. 按钮美化与位置微调 (合并最终版) */

/* === 第 1 部分：按钮位置控制 === */

/* A. 定义位置变量并应用 */
.mes .mes_buttons,
.mes .mes_edit_buttons {
    /* --- 变量定义 --- */
    /* 垂直微调：正数向下，负数向上 (通用) */
    --buttons-vertical-offset: 54px;
    --buttons-horizontal-offset: -5px;
    /* 用户专属水平微调 */
    --user-buttons-horizontal-offset: -35px;

    /* --- 变量应用 --- */
    position: relative; 
    top: var(--buttons-vertical-offset);
    left: var(--buttons-horizontal-offset);
}

/* B. 为用户消息应用专属的水平位置 */
.mes[is_user="true"] .mes_buttons,
.mes[is_user="true"] .mes_edit_buttons {
    --buttons-horizontal-offset: var(--user-buttons-horizontal-offset);
}


/* === 第 2 部分：按钮外观美化 (颜文字样式) === */

/* C. 设置颜文字的通用样式 (字体、颜色等) */
.mes_edit::before,
.extraMesButtonsHint::before {
    display: block !important;
    font-size: 0.9rem;
    color: #8b6262;
    font-weight: bold;
    font-family: "Kingnammm Maiyuan 2" !important;

}

/* D. 定义具体的颜文字内容 */
.extraMesButtonsHint::before {
    content: '♡⸝⸝•' !important; /* “更多”按钮 */
}
.mes_edit::before {
    content: '•⸝⸝♡' !important; /* “编辑”按钮 */
}

/* E. 清理原始按钮的背景和尺寸，让颜文字成为主体 */
.mes_edit,
.extraMesButtonsHint {
    background: none !important;
    width: auto !important;
    height: auto !important;
    transition: transform 0.2s ease;
    cursor: pointer;
    /* --- 新增：强制移除任何继承的阴影 --- */
    text-shadow: none !important;
    box-shadow: none !important;
}

/* F. 添加简单的悬停放大效果 */
.mes_edit:hover,
.extraMesButtonsHint:hover {
    transform: scale(1.15);
}

/* 上下装饰 */
.mes {
  position: relative;
  z-index: 1;
}
.mes_block {
  position: relative;
  z-index: 1;
}

/* 上左：压在气泡下面 */
.mes_block::before {
  content: "";
  position: absolute;
  top: -42px;
  left: -24px;
  width: 75px;
  height: 75px;
  background: url("https://p.sda1.dev/26/bf682f3b0bee772d8ebe7d90dacb39f4/1000023599.png") no-repeat center/contain;
  pointer-events: none;
  z-index: -20; /* 在气泡下面 */
}

/* 上右：保持在气泡上面 */
.mes_block::after {
  content: "";
  position: absolute;
  top: -50px;
  right: -25px;
  width: 67px;
  height: 67px;
  background: url("https://p.sda1.dev/26/377408f925610e392d3a3a0425ea0cb5/1755849442029_edit_218233569292998.png") no-repeat center/contain;
  pointer-events: none;
  z-index: 3;
  filter: drop-shadow(1px 0px 1px rgba(255, 180, 200, 0.9));
}

/* 下左 */
.mes::before {
  content: "";
  position: absolute;
  bottom: -93px;
  left: -30px;
  width: 220px;
  height: 220px;
  background: url("https://p.sda1.dev/26/b86ad615928c473821f1e957e85c9bb3/1000023648.png") no-repeat center / contain;
  pointer-events: none;
  z-index: 3;
}

/* 下右 */
.mes::after {
  content: "";
  position: absolute;
  bottom: -28px;
  right: -7px;
  width: 80px;
  height: 80px;
  background: url("https://p.sda1.dev/26/e1e7abd4b0fa165bce0dd91b619229dc/1000023650.png") no-repeat center / contain;
  pointer-events: none;
  z-index: 3;
}

/*  10. 头像、头像框 & 水平光条 */

/* 10.1 容器定位 */
.mes .mesAvatarWrapper {
    position: absolute;
    display: flex;
    z-index: 5;
    padding: 0;
    width: auto;
}

/* AI 头像位置 */
.mes:not([is_user="true"]) .mesAvatarWrapper {
    top: -90px; 
    left: 6px;  
}

/* 用户头像位置 */
.mes[is_user="true"] .mesAvatarWrapper {
    top: -90px; 
    right: 1px; 
}

/* 10.2 头像尺寸  */
.mes .avatar {
    position: relative;
    width: 80px;
    height: 80px;
    transition: transform 0.3s ease;
    flex-shrink: 0;
}
.mes .avatar:hover { transform: scale(1.05); }

.mes .avatar img {
    width: 100% !important;
    height: 100% !important;
    object-fit: cover !important;
    border-radius: 50% !important;
    background: white;
    padding: 1px;
    position: relative;
    z-index: 1;
    box-shadow: none !important;
}

/* 10.3 头像框样式 */
/* AI 头像框 */
.mes:not([is_user="true"]) .avatar::before {
    content: ""; 
    position: absolute; 
    width: 96px; 
    height: 96px;
    top: 45%; 
    left: 52%;
    transform: translate(-50%, -50%);
    background-image: url("https://p.sda1.dev/26/021144a2ffda5b001a22f1a33ef53e9f/1000023165.gif");
    background-size: contain; 
    background-repeat: no-repeat; 
    z-index: 9;
}

/* 用户头像框 */
.mes[is_user="true"] .avatar::before {
    content: ""; 
    position: absolute; 
    width: 98px; 
    height: 98px; 
    top: 43%; 
    left: 52%;
    transform: translate(-50%, -50%);
    background-image: url("https://p.sda1.dev/26/a8a7d5154839bb8e558bfa1b2788fda7/1000023164.gif");
    background-size: contain; 
    background-repeat: no-repeat; 
    z-index: 9;
}

/*10.4 水平渐隐毛玻璃光条 (开放式末端) */
.mes .avatar::after {
    content: '';
    position: absolute;
    top: 55%;
    transform: translateY(-50%);
    width: 280px; 
    height: 65px; 
    z-index: 0;
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
    box-shadow: inset 0 1.5px 1px rgba(255, 255, 255, 0.7), 
                inset 0 -1.5px 1px rgba(255, 255, 255, 0.3);
}

/* AI 的光条 (左侧圆角，右侧开放) */
.mes:not([is_user="true"]) .avatar::after {
    left: 30px;
    border-radius: 30px 0 0 30px; /* 只设置左上和左下圆角 */
    background: linear-gradient(
        to right, 
        rgba(255, 235, 240, 0.75) 10%, 
        rgba(255, 255, 255, 0) 85%
    );
}

/* 用户的光条 (右侧圆角，左侧开放) */
.mes[is_user="true"] .avatar::after {
    right: 30px;
    border-radius: 0 30px 30px 0; /* 只设置右上和右下圆角 */
    background: linear-gradient(
        to left, 
        rgba(255, 235, 240, 0.75) 10%, 
        rgba(255, 255, 255, 0) 85%
    );
}

.mes .avatar::before,
.mes .avatar::after {
  pointer-events: none !important;
}
/* 10.4 姓名框与时间戳 (悬浮于气泡之上) */

/* 核心：让气泡成为名字框的定位锚点 */
.mes_block {
    position: relative !important;
    overflow: visible !important; 
}

/* 名字和时间戳的容器，绝对定位到气泡外部 */
.mes .ch_name {
    position: absolute;
    top: -89px;      
    z-index: 5;
    pointer-events: auto;
    width: 250px;
}

/* AI 的名字框：向右移动 */
.mes:not([is_user="true"]) .ch_name {
    left: 93px;      
}

/* 用户的名字框：向右移动 (减小数值) */
.mes[is_user="true"] .ch_name {
    right: -32px;   
}

/* --- 关键修正：强制内部垂直排列 --- */
.mes .ch_name .alignItemsBaseline {
    flex-direction: column !important;
}
.mes:not([is_user="true"]) .ch_name .alignItemsBaseline {
    align-items: flex-start !important;
}
.mes[is_user="true"] .ch_name .alignItemsBaseline {
    align-items: flex-end !important;
}

/* 姓名样式 (大小统一) */
.mes .name_text {
    font-size: 0.9em; /* 调整为统一大小 */
    font-weight: normal; /* 去掉加粗 */
    color: rgba(85, 85, 85, 0.9);
}

/* 时间戳样式 (大小统一，更紧凑) */
.mes .timestamp {
    font-size: 0.8em; /* 调整为统一大小 */
    color: rgba(85, 85, 85, 0.6);
    margin-top: -6px; /* 间距调为0 */
}
.mes_reasoning_actions.flex-container,
.mes_buttons,
.flex-container.swipeRightBlock.flexFlow{
